import React, { Component } from 'react';
import AceEditor from 'react-ace';
import Giveback from './OP';
import axios from 'axios';
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-monokai";
import "ace-builds/src-noconflict/theme-dracula";
import "ace-builds/src-noconflict/ext-language_tools"

class App extends Component{
  constructor(props) {
    super(props);
    this.state = { apiResponse: "" };
}

callAPI() {
    fetch("http://localhost:3001/api")
        .then(res => res.text())
        .then(res => this.setState({ apiResponse: res }));
}

componentWillMount() {
    this.callAPI();
}
  state={
    code:'',
    results:''
  }

  handleRun = () =>{
    var url="http://localhost:3001/api/code";
    const codeData=this.state.code;
    let formData = new FormData(); 
    formData.append('jscode', codeData);
    const config = {     
      headers: {
        'Content-type': 'application/json'
      }
  }
    console.log(formData);
    axios.post(url, formData, config)
    .then(response => {
        var temp=response.data.stdout;
        this.setState({
          results:temp.toString()
        })
        console.log(temp);
    })
    .catch(error => {
        console.log("problem!");
    });

  }


  handleChange = (newVal) =>{
    this.setState({
      code:newVal
    })
  }

  render(){
    return(
      <div>
      <p>{this.state.apiResponse}</p>
      <AceEditor     
      mode="javascript"
      width='1000px'
      height='500px'
      theme="monokai"
      onChange={this.handleChange}
      editorProps={{ $blockScrolling: true }}
      setOptions={{
        enableBasicAutocompletion: true,
        enableLiveAutocompletion: true,
        enableSnippets: true
      }}
      /> 
      <button onClick={this.handleRun}>RUN CODE</button>
      <AceEditor
            placeholder="Output will be shown here"
            mode="text"
            theme="dracula"
            name="code-results"
            value={this.state.results}
            showPrintMargin={false}
            showGutter={false}
            highlightActiveLine={false}            
            readOnly={true}
            editorProps={{ $blockScrolling: true }}
          />  

    </div>
    );
  }
}

export default App;
